import {BrowserRouter as Router, Routes, Route, NavLink } from 'react-router-dom'
import '../styles/portfolio.css';
import Thumbnail from './Thumbnail';

export default class Portfolio extends Component {
    render () {
        const { projectList, match } = this.props
        return (
            <Fragment>
                <section id="portfolio">
                    <NavLink to={`/`} id="backbtn">back â†©</NavLink>
                    <div class="container-grey gallery">
                        { Object.keys(projectList).map(id=> {
                            return (
                                    <Thumbnail
                                    key={id}
                                    img={projectList[id].imgSource}
                                    name={projectList[id].title}
                                    description={projectList[id].description}
                                    period={projectList[id].period}
                                    tags={projectList[id].tags}
                                    url={`/portfolio/${id}`}
                                    />
                            )
                        }) }
                    </div>
                </section>
            </Fragment>
        )
    }
}
